/**
 * Enqueue Google Web Fonts.
 */
function megafactory_theme_default_fonts_url() {
    $font_url = '';
    
    /*
    Translators: If there are characters in your language that are not supported
    by chosen font(s), translate this to 'off'. Do not translate into your own language.
     */
    if ( 'off' !== _x( 'on', 'Google font: on or off', 'megafactory' ) ) {
        $font_url = add_query_arg( 'family', urlencode( 'Roboto:400,300,500&subset=latin,latin-ext' ), "//fonts.googleapis.com/css" );
    }
    return $font_url;
}
function megafactory_enqueue_google_web_fonts() {
	wp_enqueue_style( 'google-fonts', megafactory_theme_default_fonts_url(), array(), null, 'all' );
}
function megafactory_default_theme_options(){
	$cur_theme = wp_get_theme();	
	if ( $cur_theme->get( 'Name' ) == 'Megafactory' || $cur_theme->get( 'Name' ) == 'Megafactory Child' ){

		$input_val = megafactory_default_theme_values();
		$megafactory_options = json_decode( $input_val, true );
		update_option( 'megafactory_options', $megafactory_options );
		megafactory_save_theme_options();

	}
}
if ( ! class_exists( 'MegafactoryRedux' ) ) {
	add_action( 'wp_enqueue_scripts', 'megafactory_enqueue_google_web_fonts' );
	add_action("after_switch_theme", "megafactory_default_theme_options", 10);
}
